{
  \inst{1}%
  Department of Political Science\\
  National Taiwan University
  \and
  \inst{2}%
  Department of Economics\\
  National Taiwan University
}

\date[2024] % 選填
{Very Large Conference, April 2024}

\begin{document}

    \begin{frame}
        \maketitle % 也可以使用 \titlepage 或 \frame{\titlepage}
    \end{frame}

    \begin{frame}
        \tableofcontents
    \end{frame}

    \section{Introduction}
